import styled from "styled-components";
export const OptionsContainer = styled.div`
  display: flex;
  justify-content: center;
  margin: 3rem 0 2rem 0;
  @media screen and (max-width: 700px) {
    flex-direction: column;
    align-items: center;
  }
`;
